mapper.select:
SELECT MEM_ID memId,MEM_PW memPw,MAM_ADDRESS memAddress, MEM_NAME memName, MEM_PHONE memPhone, MEM_BIRTH memBirth, MEM_GENDER memGender, MEM_ACCOUNT memAccount, MEM_EMAIL memEmail, MEM_EMAIL_CK memEmailCk, POST_NUMBER postNumber, DETAIL_ADD detailAdd
FROM MEMBER

	WHERE MEM_ID = #{memId}

mapper.update:
UPDATE MEMBER
SET POST_NUMBER = #{postNumber}, MAM_ADDRESS= #{memAddress}, DETAIL_ADD = #{detailAdd}, MEM_EMAIL = #{memEmail} , MEM_EMAIL_CK = #{memEmailCk} , MEM_PHONE = #{memPhone}, MEM_ACCOUNT = #{memAccount}
WHERE MEM_ID = #{memId}

mapper.select:
